import {ILPNFTStakeFarm} from "../../interfaces/defi/ILPNFTStakeFarm.sol";
import {ILPSFTLendFarm} from "../../interfaces/defi/ILPSFTLendFarm.sol";
import {IUniV3StakeFarm} from "../../interfaces/defi/IUniV3StakeFarm.sol";
