pragma solidity 0.8.28;

import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";

import {IDutchAuctionActions} from "./IDutchAuctionActions.sol";
import {IDutchAuctionAdminActions} from "./IDutchAuctionAdminActions.sol";
import {IDutchAuctionErrors} from "./IDutchAuctionErrors.sol";
import {IDutchAuctionEvents} from "./IDutchAuctionEvents.sol";
import {IDutchAuctionRoutes} from "./IDutchAuctionRoutes.sol";
import {IDutchAuctionState} from "./IDutchAuctionState.sol";

/**
 * @title The interface for the Bureau of the Dutch Auction
 */
interface IDutchAuction is
  IERC165,
  IDutchAuctionEvents,
  IDutchAuctionErrors,
  IDutchAuctionRoutes,
  IDutchAuctionState,
  IDutchAuctionAdminActions,
  IDutchAuctionActions
{}

/*
 * Copyright (C) 2024 Powell Nickels
 * https://github.com/PowellNickels/pow5-contracts
 *
 * SPDX-License-Identifier: GPL-3.0-or-later
 * See the file LICENSE.txt for more information.
 */

pragma solidity 0.8.28;

import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";

/**
 * @title Bureau of the Dutch Auction, Public Action Interface
 */
interface IDutchAuctionActions is IERC165 {
  //////////////////////////////////////////////////////////////////////////////
  // Public interface
  //////////////////////////////////////////////////////////////////////////////

  /**
   * @dev Purchase and stake an LP-NFT at the current auction price
   *
   * If either `pow1Amount` or `marketTokenAmount` are zero, the purchase will
   * be done via single-sided supply; about half of one token is swapped for the
   * other before pooling. If neither are zero, the tokens will be supplied to
   * the pool with no swap, and any unconsumed tokens (due to an imbalance with
   * the current pool price) will be returned to the sender.
   *
   * Upon purchase, the LP-NFT is staked in a stake farm, and an LP-SFT is
   * minted to the receiver.
   *
   * @param lpNftTokenId The LP-NFT token ID to purchase
   * @param pow1Amount The amount of the game token to deposit
   * @param marketTokenAmount The amount of the market token to deposit
   * @param beneficiary The beneficiary of the tip paid as part of the auction
   * @param receiver The receiver of the LP-SFT
   */
  function purchase(
    uint256 lpNftTokenId,
    uint256 pow1Amount,
    uint256 marketTokenAmount,
    address beneficiary,
    address receiver
  ) external;
}

/*
 * Copyright (C) 2024 Powell Nickels
 * https://github.com/PowellNickels/pow5-contracts
 *
 * SPDX-License-Identifier: GPL-3.0-or-later
 * See the file LICENSE.txt for more information.
 */

pragma solidity 0.8.28;

import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";

import {IDutchAuctionState} from "./IDutchAuctionState.sol";

/**
 * @title Bureau of the Dutch Auction, Admin Action Interface
 */
interface IDutchAuctionAdminActions is IERC165 {
  //////////////////////////////////////////////////////////////////////////////
  // Admin interface
  //////////////////////////////////////////////////////////////////////////////

  /**
   * @dev Initialize the Dutch Auction
   *
   * The Dutch Auction is initialized my minting the first POW1 LP-NFT and its
   * holding LP-SFT. No creator tip is paid.
   *
   * It is assumed that this will be the first liquidity deposited in the pool,
   * so both pow1Amount and marketTokenAmount are required to be non-zero.
   *
   * @param pow1Amount The amount of the game token to deposit
   * @param marketTokenAmount The amount of the asset token to deposit
   * @param receiver The receiver of the POW1 LP-SFT
   *
   * @return nftTokenId The initial LP-NFT/LP-SFT token ID
   */
  function initialize(
    uint256 pow1Amount,
    uint256 marketTokenAmount,
    address receiver
  ) external returns (uint256 nftTokenId);

  /**
   * @dev Check if the Dutch Auction is initialized
   *
   * @return True if the Dutch Auction is initialized, false otherwise
   */
  function isInitialized() external view returns (bool);

  /**
   * @dev Set the total number of LP-NFTs for sale
   *
   * If this is greater than the current number of LP-NFTs, the difference will
   * be minted and added to the sale. If it is less, LP-NFTs won't be minted to
   * replace the current ones as they are sold.
   *
   * @param auctionCount The target number of LP-NFTs for sale
   * @param marketTokenDust Some dust to produre, if needed, to mint the LP-NFTs
   */
  function setAuctionCount(
    uint32 auctionCount,
    uint256 marketTokenDust
  ) external;

  /**
   * @dev Get the total number of LP-NFTs for sale
   *
   * @return The total number of LP-NFTs for sale
   */
  function getAuctionCount() external view returns (uint32);
}

/*
 * Copyright (C) 2024 Powell Nickels
 * https://github.com/PowellNickels/pow5-contracts
 *
 * SPDX-License-Identifier: GPL-3.0-or-later
 * See the file LICENSE.txt for more information.
 */

pragma solidity 0.8.28;

/**
 * @title Bureau of the Dutch Auction, Error Interface
 *
 * @dev This includes errors for both public actions and admin actions
 */
interface IDutchAuctionErrors {
  //////////////////////////////////////////////////////////////////////////////
  // Admin Errors
  //////////////////////////////////////////////////////////////////////////////

  error DutchAuctionNotInitialized();

  error DutchAuctionAlreadyInitialized();

  //////////////////////////////////////////////////////////////////////////////
  // Public Errors
  //////////////////////////////////////////////////////////////////////////////

  error AuctionNotEnabled(uint256 slot);
}

/*
 * Copyright (C) 2024 Powell Nickels
 * https://github.com/PowellNickels/pow5-contracts
 *
 * SPDX-License-Identifier: GPL-3.0-or-later
 * See the file LICENSE.txt for more information.
 */

pragma solidity 0.8.28;

import {IDutchAuctionState} from "./IDutchAuctionState.sol";

/**
 * @title Bureau of the Dutch Auction, Event Interface
 */
interface IDutchAuctionEvents {
  //////////////////////////////////////////////////////////////////////////////
  // Events
  //////////////////////////////////////////////////////////////////////////////

  /**
   * @dev Event to emit when the Bureau of the Dutch Auction is initialized
   *
   * @param lpNftTokenId The token ID of the LP-NFT minted in the initialization
   * @param gameTokenAmount The amount of game tokens used to initialize the pool
   * @param assetTokenAmount The amount of asset tokens used to initialize the pool
   * @param receiver The address of the receiver of the LP-SFT holding the staked LP-NFT
   */
  event AuctionInitialized(
    uint256 lpNftTokenId,
    uint256 gameTokenAmount,
    uint256 assetTokenAmount,
    address indexed initializer,
    address indexed receiver
  );

  // TODO
  /**
   * @dev Event to emit when a new auction is created
   *
   * @param lpNftTokenId The token ID of the LP-NFT minted for the auction
   * @param auctionStartTime The time when the auction started
   * @param vrgdaParams The VRGDA parameters for the auction schedule
   *
  event AuctionCreated(
    uint256 indexed lpNftTokenId,
    uint256 auctionStartTime,
    IDutchAuctionState.VRGDAParams vrgdaParams
  );

  /**
   * @dev Event to emit when an LP-NFT is purchased
   *
   * @param lpNftTokenId The token ID of the LP-NFT purchased
   * @param liquidityAmount The amount of LPPOW1 tokens in the purchased LP-NFT
   * @param beneficiaryTip The beneficiary tip, in bips scaled by 1e18
   * @param buyer The address of the buyer of the LP-NFT
   * @param receiver The address of the receiver of the LP-NFT
   *
  event AuctionPurchased(
    uint256 indexed lpNftTokenId,
    uint128 liquidityAmount,
    uint256 beneficiaryTip,
    address indexed buyer,
    address indexed receiver
  );

  /**
   * @dev Event to emit when an LP-NFT position is exited, returning the
   * underlying assets in the form of the asset token
   *
   * @param lpNftTokenId The token ID of the LP-NFT exited
   * @param operator The address exiting the LP-NFT position
   *
  event AuctionExited(uint256 indexed lpNftTokenId, address indexed operator);
  */
}

