    uint256 assetTokenAmount,
    address recipient
  ) public override nonReentrant returns (uint256 lpNftTokenId) {
    // Call ancestor
    uint256 gameTokenShare;
    uint256 assetTokenShare;
    uint128 liquidityAmount;
    (
      lpNftTokenId,
      gameTokenShare,
      assetTokenShare,
      liquidityAmount
    ) = _mintLpNftWithDenominatorToken(assetTokenAmount, recipient);

    // Dispatch event
    emit LpNftMinted(
      _msgSender(),
      recipient,
      address(_numeratorToken),
      address(_denominatorToken),
      address(_uniswapV3NftManager),
      lpNftTokenId,
      gameTokenShare,
      assetTokenShare,
      liquidityAmount
    );

    return lpNftTokenId;
  }

  /**
   * @dev See {IGameTokenPooler-mintLpNftImbalance}
   */
  function mintLpNftImbalance(
    uint256 gameTokenAmount,
    uint256 assetTokenAmount,
    address recipient
  ) public override nonReentrant returns (uint256 lpNftTokenId) {
    // Call ancestor
    uint256 gameTokenShare;
    uint256 assetTokenShare;
    uint128 liquidityAmount;
    (
      lpNftTokenId,
      gameTokenShare,
      assetTokenShare,
      liquidityAmount
    ) = _mintLpNftImbalance(gameTokenAmount, assetTokenAmount, recipient);

    // Dispatch event
    emit LpNftMinted(
      _msgSender(),
      recipient,
      address(_numeratorToken),
      address(_denominatorToken),
      address(_uniswapV3NftManager),
      lpNftTokenId,
      gameTokenShare,
      assetTokenShare,
      liquidityAmount
    );

    return lpNftTokenId;
  }

