   * @dev See {IGameTokenPooler-collectFromLpNft}
   */
  function collectFromLpNft(
    uint256 lpNftTokenId,
    address recipient
  ) public override nonReentrant returns (uint256 assetTokenReturned) {
    // Call ancestor
    uint128 liquidityAmount;
    uint256 gameTokenCollected;
    uint256 assetTokenCollected;
    (
      liquidityAmount,
      gameTokenCollected,
      assetTokenCollected,
      assetTokenReturned
    ) = _collectFromLpNft(lpNftTokenId, recipient);

    // Dispatch event
    emit LpNftCollected(
      _msgSender(),
      recipient,
      address(_numeratorToken),
      address(_denominatorToken),
      address(_uniswapV3NftManager),
      lpNftTokenId,
      liquidityAmount,
      gameTokenCollected,
      assetTokenCollected,
      assetTokenReturned
    );

    return assetTokenReturned;
  }

  /**
   * @dev See {IGameTokenPooler-exit}
   */
  function exit(
    uint256 lpNftTokenId
  ) public override nonReentrant returns (uint256 assetTokenReturned) {
    // Call ancestor
    uint128 liquidityAmount;
    uint256 gameTokenCollected;
    uint256 assetTokenCollected;
    (
      liquidityAmount,
      gameTokenCollected,
      assetTokenCollected,
      assetTokenReturned
    ) = _exitPooler(lpNftTokenId);

    // Dispatch event
    emit LpNftCollected(
      _msgSender(),
      _msgSender(),
      address(_numeratorToken),
      address(_denominatorToken),
      address(_uniswapV3NftManager),
      lpNftTokenId,
      liquidityAmount,
      gameTokenCollected,
      assetTokenCollected,
      assetTokenReturned
    );

    return assetTokenReturned;
  }
}

