    address account,
    uint256[] memory nftTokenIds
  ) external {
    // Translate parameters
    uint256[] memory tokenAmounts = ERC1155Helpers.getAmountArray(
      nftTokenIds.length
    );

    // Call ancestor
    _mintBatch(account, nftTokenIds, tokenAmounts, "");
  }

  //////////////////////////////////////////////////////////////////////////////
